package day10_controlFlowStatements_Part4;

public class task_46_MultiplcationTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=7;
		
		System.out.println("Multiplcation table for " + num);
		System.out.println("----------------------------");
		
		for (int x=1; x<=10; x++) {
			
			System.out.println(num + " * " + x + " = " + num*x);
		}
	}

}
