package day11_controlFlowStatements;

public class task_47_createPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int x=0; x<7; x++  ) {
						
				for (int j=1; j<=6; j++) {
					System.out.print("*"); //print yaparsan (ln - silersen) yan yana yazar.
				}
				
				System.out.println(""); //print+ln koyduðumuz için her 6. * dan sonra bir boþ yapýp alt satýra kaydýracak.
		}
	}

}
